sys_transform_script.script:
if (source.u_resource_ids) {
	ignore = true;
	var ids = source.u_resource_ids.toString();
	var deployments = new GlideRecord('x_ris_rightscale_c_deployments');
	deployments.addQuery('u_x_ris_rightscale_c_account_id', source.u_account_id);
	deployments.query();
	while (deployments.next()) {
		if (ids.indexOf(deployments.u_id) < 0) {
			if (deployments.deleteRecord())
				gs.info("Deployment with id '" + deployments.u_id + "' successfully deleted." );
		}
	}
}
