sysauto_script.script:
var authenticator = new RightScaleAuthenticator();
var helper = new RightScaleHelper();
var ready = helper.readyForUpdate(current.run_start, 'instance');
var forceUpdateMessage = "";
var account = new GlideRecord('x_ris_rightscale_c_accounts');
account.query();
while (account.next()) {
	if (account.instances_updated_at) {
		if (!ready) continue;
	} else {
		forceUpdateMessage = "Force ";
	}
	gs.info("Executing Scheduled Job: RightScale " + forceUpdateMessage + "Update Deployments and Instances for account " + account.u_id);
	if (!authenticator.validateAccessToken(account)){
		if (!authenticator.updateAccountAccessToken(account)) continue;
	}
	try {
		var deployments = new RightScaleResource('deployments');
		var rsDeployments = deployments.getRSResource(account);
		if (!rsDeployments) {
			gs.error("Updating Deployments/Instances failed for account " + account.u_id + " due to unsuccessful response to RS API.");
			continue;
		}
		deployments.updateDeploymentsAndInstances(rsDeployments, account);
	} catch(ex) {
		gs.error("Updating Deployments/Instances failed for account " + account.u_id + " with error: " + ex.toString());
	}
}
