sysauto_script.script:
var authenticator = new RightScaleAuthenticator();
var helper = new RightScaleHelper();
var ready = helper.readyForUpdate(current.run_start, 'application');
var forceUpdateMessage = "";
var account = new GlideRecord('x_ris_rightscale_c_accounts');
account.query();
while (account.next()) {
	if (account.applications_updated_at) {
		if (!ready) continue;
	} else {
		forceUpdateMessage = "Force ";
	}
	gs.info('Executing Scheduled Job: Self-Service ' + forceUpdateMessage + 'Update Applications for account ' + account.u_id);

	if (!authenticator.validateAccessToken(account)){
		if (!authenticator.updateAccountAccessToken(account)) continue;
	}

	try {
		// login to SS every time to make sure a session is not expired
		var responseState = authenticator.loginToSelfService(account);
		if (responseState != 200) {
			gs.error("Updating Applications failed for account " + account.u_id + " due to unsuccessful login to Self-Service.");
			continue;
		}

		var applications = new RightScaleResource('applications', 'catalog');
		var ssApplications = applications.getSSResource(account);
		if (!ssApplications) {
			gs.error("Updating Applications failed for account " + account.u_id + " due to unsuccessful response to RS API.");
			continue;
		}
		applications.updateAll(ssApplications, account);
	} catch(ex) {
		gs.error("Updating Applications failed for account " + account.u_id + " with error: " + ex.toString());
	}
}
