sysauto_script.script:
var authenticator = new RightScaleAuthenticator();
var helper = new RightScaleHelper();
var ready = helper.readyForUpdate(current.run_start, 'execution');
var forceUpdateMessage = "";
var account = new GlideRecord('x_ris_rightscale_c_accounts');
account.query();
while (account.next()) {
	if (account.executions_force_update) {
		forceUpdateMessage = "Force ";
		account.executions_force_update = false;
		account.update();
	} else if (!ready) {
		continue;
	}
	gs.info("Executing Scheduled Job: Self-Service " + forceUpdateMessage +  "Update Executions for account " + account.u_id);
	if (!authenticator.validateAccessToken(account)){
		if (!authenticator.updateAccountAccessToken(account)) continue;
	}

	try {
		// login to SS every time to make sure a session is not expired
		var responseState = authenticator.loginToSelfService(account);
		if (responseState != 200) {
			gs.error("Updating Executions failed for account " + account.u_id + " due to unsuccessful login to Self-Service.");
			continue;
		}
		var executions = new RightScaleResource('executions', 'manager');
		var ssExecutions = executions.getSSResource(account);
		if (!ssExecutions) {
			gs.error("Updating Executions failed for account " + account.u_id + " due to unsuccessful response to RS API.");
			continue;
		}
		executions.updateAll(ssExecutions, account);
	} catch(ex) {
		gs.error("Updating Executions failed for account " + account.u_id + " with error: " + ex.toString());
	}
}
