answer = ifScript();

function ifScript() {
	var authenticator = new RightScaleAuthenticator();
	var app = new GlideRecord('x_ris_rightscale_c_applications');
	app.get(current.variables.u_application);
	workflow.scratchpad.application_id = app.getValue('u_id').toString();
	workflow.scratchpad.required_parameters = app.getValue('u_required_parameters').toString();
	workflow.scratchpad.parameters = app.getValue('parameters').toString();	
	var account = new GlideRecord('x_ris_rightscale_c_accounts');
	account.get(app.u_account);
	workflow.scratchpad.account_id = account.getValue('u_id').toString();
	if (authenticator.validateAccessToken(account)){
		var helper = new RightScaleHelper();
		workflow.scratchpad.accessToken = account.u_access_token.getDecryptedValue();
		workflow.scratchpad.baseUrl = account.getValue('u_ss_endpoint').toString();
		return 'yes';
	}
	return 'no';
}

