var account = new GlideRecord('x_ris_rightscale_c_accounts');
if (account.get(workflow.scratchpad.account_id)){
	var r = new sn_ws.RESTMessageV2('x_ris_rightscale_c.Self-Service Application Launch', 'post');
	var helper = new RightScaleHelper();
	r.setStringParameter('accountId', account.u_id);
	r.setStringParameter('applicationId', workflow.scratchpad.application_id);
	r.setStringParameter('apiVersion', account.u_ss_api_version);
	r.setStringParameter('baseUrl', account.u_ss_endpoint);
	r.setStringParameter('accessToken', account.u_access_token.getDecryptedValue());
	var content = "{ \"name\": \"" + current.variables.u_name + "\", ";

	if( typeof current.variables.u_description != "undefined" ) {
		content += "\"description\": \"" + current.variables.u_description + "\",";
	}

	content += "\"options\": [" + workflow.scratchpad.options + "]";
	
	// If there is no schedule variable, or if it's blank, don't include it in the call. The semantic
	//  is that a blank value = no schedule
	if( typeof current.variables.u_schedule_name != "undefined" && current.variables.u_schedule_name != "" ) {
		content += ", \"schedule_name\": \"" + current.variables.u_schedule_name + "\"";
	}
	content += "}";
	
	r.setRequestBody(content);
	var response = r.execute();
	
	var responseBody = response.getBody();
	var httpStatus = response.getStatusCode();
	if( httpStatus > 399 ) {
		gs.error(responseBody);
		gs.error(httpStatus);
	}
	workflow.scratchpad.httpStatus = httpStatus;
	workflow.scratchpad.executionHref = response.getHeader('Location');
}

