	var authenticator = new RightScaleAuthenticator();
	authenticator.loginToSelfService(account);
}

var account = new GlideRecord('x_ris_rightscale_c_accounts');
if (account.get(workflow.scratchpad.account_id)){
	var authenticator = new RightScaleAuthenticator();
	authenticator.loginToSelfService(account);
}

var params = new global.JSON().decode(workflow.scratchpad.required_parameters);
var options = [];
for (var i = 0; i < params.length; i++) {
	var required_param_name = params[i];
	var param_value = current.variables[required_param_name];
	if (param_value) {
		var allParams = new global.JSON().decode(workflow.scratchpad.parameters);
		var type;
		for (var i1 = 0; i1 < allParams.length; i1++) {
			if (allParams[i1].name == required_param_name) {
				type = allParams[i1].type;
				break;
			}
		}
		var hash = "{ \"name\": \"" + required_param_name + "\", ";
		hash += "\"type\": \"" + type + "\",";
		hash += "\"value\": \"" + param_value + "\"}";
		options.push(hash);
	}
}
workflow.scratchpad.options = options;

