var authenticator = new RightScaleAuthenticator();
var account = new GlideRecord('x_ris_rightscale_c_accounts');
if (account.get('u_id', workflow.scratchpad.account_id)){	
	authenticator.updateAccountAccessToken(account);
	var helper = new RightScaleHelper();
	workflow.scratchpad.accessToken = account.u_access_token.getDecryptedValue();
	workflow.scratchpad.baseUrl = account.getValue('u_ss_endpoint').toString();
}

