}

if (workflow.scratchpad.httpStatus == 204) {
	var shortMessage = "Application '" + current.variables.u_name + "' launched successfully";
	var fullMessage = "New execution '" + workflow.scratchpad.executionHref + "' created.";
	gs.addInfoMessage(shortMessage);
	gs.addInfoMessage(fullMessage);
} else {
	gs.addInfoMessage("Launching Application failed with status: " + workflow.scratchpad.httpStatus);
}

